                        <div className="accept-delete">                    
                            <div>
                                <button className="submit-btn" onClick={handleSubmit}>Yes</button>
                                <button className="submit-btn" onClick={handleCancel}>No</button>
                            </div>
                        </div>
                    </div>
                </div> */}

            </div>

            <h2>Question Answered</h2>
            <div id="question" >
                <h2 className="title">{question.title}</h2>                      

                <ul className="keywords">
                    {
                    question.keywords.map(({ word }) =>
                        <li>{word}</li>
                    )
                    }
                </ul>
                <div><span className="small-caps">answered on: </span> {question.questionedOn}</div>

            </div>
        </li>
    );
}

export default MyAnswer;
